int CMainFrame::OnCreate(LPCREATESTRUCT lpCreateStruct)
{
	if (CFrameWnd::OnCreate(lpCreateStruct) == -1)
		return -1;
	
	if (!m_wndToolBar.CreateEx(this, TBSTYLE_FLAT, WS_CHILD | WS_VISIBLE | CBRS_TOP
		| CBRS_GRIPPER | CBRS_TOOLTIPS | CBRS_FLYBY | CBRS_SIZE_DYNAMIC) ||
		!m_wndToolBar.LoadToolBar(IDR_MAINFRAME))
	{
		TRACE0("Failed to create toolbar\n");
		return -1;      // fail to create
	}

	if (!m_wndStatusBar.Create(this) ||
		!m_wndStatusBar.SetIndicators(indicators,
		  sizeof(indicators)/sizeof(UINT)))
	{
		TRACE0("Failed to create status bar\n");
		return -1;      // fail to create
	}

	CString msg;
	msg.Format(_T("Thank you for choosing LightsUp!\n\n"
		"LightsUp! is Free Open Source Software and is provided AS IS with no warranty.\n"
		"The user assumes all risk from applying this software both as intended and in creative ways.\n"
		"The authors are not liable for any damages, personal or monetary, arising from the use of this software.\n"
		"This software is not authorized for use in safety critical situations including but not limited to life support or pyrotechnics.\n"
		"Use of this software indicates acceptance of these terms."));
	MessageBox(msg, _T("LightsUp!"));

	return 0;
}

BOOL CMainFrame::PreCreateWindow(CREATESTRUCT& cs)
{
	if( !CFrameWnd::PreCreateWindow(cs) )
		return FALSE;
	// TODO: Modify the Window class or styles here by modifying
	//  the CREATESTRUCT cs
	cs.style &= ~WS_THICKFRAME;

	return TRUE;
}

